sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue == '1' || newValue == '2') {
        g_form.setValue('state', '15');
		g_form.setValue('short_description',"As the priority is critical or high  the request is approved");
    }
    if (newValue == '4' || newValue == '5') {
        g_form.setValue('state', '13');
		g_form.setValue('short_description',"As the priority is low or planning  the request is accepted and takes long time for approval");
    }
    if (newValue == '3') {
        g_form.setValue('state', '14');
		g_form.setValue('short_description',"As the priority is moderate  the request is awaiting for approval");
    }
}
